	@POST
	@Consumes(MediaType.APPLICATION_JSON)
	@Path("/param")
	public String convertTranscriptFile(@Context HttpServletRequest req, String encodedString) {
				
	//	encodedString = "";
	//	String encodedString = (String) params.get("param");
		
		System.out.println("encodedString:" + encodedString);
		byte[] decoded = Base64.decodeBase64(encodedString);
		path= req.getServletContext().getRealPath("/WEB-INF/classes");
		System.out.println("path :" + path);
		try
		{
			//Stringpath=req.getServletContext().getRealPath("/WEB-INF/somedir/hdfeeh");
			decodedAudiofile = new File( path+"/decodefile.wav");
			//decodedAudiofile = new File( "./decodefile.wav");
			FileOutputStream os = new FileOutputStream(decodedAudiofile, true);
			os.write(decoded);
			os.close();
		}
		catch (Exception e)
		{
			e.printStackTrace();
		}


		/* * RecognizeOptions.Builder builder=new RecognizeOptions.Builder();
		 * RecognizeOptions recognizeOptions=new RecognizeOptions(builder);*/


		SpeechToText service = new SpeechToText();
		System.out.println("setting username and password");

		service.setUsernameAndPassword("e93c5772-a17d-4402-8368-c46dea7402ec", "7QMICowkjvqs");
		service.setEndPoint("https://stream.watsonplatform.net/speech-to-text/api");

		//audio = new File("C:\\HackathonVideo\\r.wav");
		FileInputStream inputStream;

		try {
			inputStream = new FileInputStream(decodedAudiofile);		

			RecognizeOptions options = new RecognizeOptions.Builder().continuous(true).interimResults(true).interimResults(false)
					.contentType(HttpMediaType.AUDIO_WAV).build();						
			service.recognizeUsingWebSocket(inputStream, options, new BaseRecognizeCallback() {
				@Override
				public void onTranscription(SpeechResults speechResults)  {
					result = "";
					try {

						fw= new FileWriter(path+"/decodefile.txt");
						//fw= new FileWriter("./decodefile.txt");

						for(Transcript t : speechResults.getResults()) {

							result = result.concat(t.getAlternatives().get(0).getTranscript());
							try {
								fw.write(result+"\n");
							} catch (IOException e) {
								e.printStackTrace();
							} 
							System.out.println("Audio converted to text :" + result);
						}

						fw.close();

					} catch (IOException e) {
						e.printStackTrace();
					}
				}
			});

			// wait 20 seconds for the asynchronous response
			Thread.sleep(20000);

			inputStream.close();
		} catch (Exception e) {
			e.printStackTrace();
		}

		return result ;

	}



